import React from 'react';
import {
  StyleSheet,
  TextInput,
  ActivityIndicator,
  Text,
  View,
  NavigationContainer,
  createStackNavigator,
  TouchableOpacity,
} from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';


const validationSchema = yup.object().shape({
    email:yup
    .string()
    .label('Email')
    .email('Format incorrecte.')
    .required('Email obligatoire.'),
});

export  function MdpOubliéScreen({navigation}){
  return (

    <View style={styles.container}>
 
      <Text style={styles.titre}>Retrouvez Votre Compte</Text>


<Text style={styles.saisie}>Veuillez saisir votre adresse e-mail:</Text>
<Formik
  initialValues={{ email: '' }}
  onSubmit={(values, actions) => {
    alert(JSON.stringify(values));
    setTimeout(() => {
      actions.setSubmitting(false);
    }, 1000);
  }}
  validationSchema={validationSchema}
  >
  {(formikProps) => (

    <React.Fragment>

<TextInput 
        placeholder='Ecrivez votre Email' 
        placeholderTextColor='#808080'
        
        style={styles.textInput}
        onChangeText={formikProps.handleChange('email')}
        onBlur={formikProps.handleBlur('email')}
        autoFocus
    />
        <Text style={{ color: 'red',marginLeft:10  }}>
              {formikProps.touched.email && formikProps.errors.email}
        </Text>


<View style={styles.vButton}>
{formikProps.isSubmitting ? (
                  <ActivityIndicator />
                  ) : (
                  <TouchableOpacity
                      onPress={formikProps.handleSubmit}>
                    <Text style={styles.recup}>Récupérer</Text>
                  </TouchableOpacity>
                   )}
 <TouchableOpacity
  onPress={() => {navigation.navigate('AdminScreen')}}>
     <Text style={styles.annul}>Annuler</Text>
 </TouchableOpacity>
 </View>
 
 </React.Fragment>
)}
</Formik>
      </View>
  );}

const styles=StyleSheet.create({

    container:{ 
    flex:1,
    alignItems:'center',
    
    },
    titre:{
        fontSize:30,
        fontWeight:'bold',
        marginTop:150,
        color:'#367ce5',
    },
    saisie:{
        marginTop:120,
        fontWeight:'bold',
        fontSize:17,
    },
    textInput:{
        marginTop:20,
        borderWidth:1,
        textAlign:'center',
        width:240,
        height:35,
        letterSpacing:2,
    },
    vButton:{
    marginTop:50,
    flexDirection:'row',
    margin:10,
    },
    recup:{
    marginRight:20,
    backgroundColor:'#367ce5',
    color:'white',
    fontSize:18,
    width:150,
    textAlign:'center',
    height:30,
    borderRadius:15,
    },
    annul:{
        backgroundColor:'#367ce5',
        color:'white',
        fontSize:18,
        width:150,
        textAlign:'center',
        height:30,
        borderRadius:15,
    }

});
