import React from 'react';
import {View,StyleSheet} from 'react-native';
import {
    DrawerContentScrollView,
    DrawerItem
}
from '@react-navigation/drawer';
import {
    Avatar,
    Title,
    Caption,
    Drawer,
    Text,
    TouchableRipple,
    Switch,
}from 'react-native-paper';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';



export function DrawerContent(props){
    const [isDarkTheme,setIsDarkTheme]=React.useState(false);
    const toggleTheme=() =>{
        setIsDarkTheme(!isDarkTheme);
    }
    return(
        <View style={{flex:1}}>
<DrawerContentScrollView {...props}>
    <View style={styles.drawerContent}>
        <View style={styles.userInfoSection}>
            <View style={{flexDirection:'row',marginTop:15}}>
                <Avatar.Image
                source={{
                   uri:'https://image.flaticon.com/icons/png/512/147/147144.png'
                }}
                size={50}
                />
                <View style={{marginLeft:15,flexDirection:'column'}}>
                    <Title style={styles.title}>Emir Gribaa</Title>
                    <Caption style={styles.caption}>Zagalawlaw</Caption>
                </View>
            </View>
            <View style={styles.row}></View>

        </View>
        <Drawer.Section style={styles.bottomDrawerSection}>
           
           <DrawerItem
           icon={({color,size})=> (
               <Icon 
               name="home-outline"
               color={color}
               size={size}
               
               />
       
           )}
               label='Accueil'
               onPress={()=> {props.navigation.navigate('Home')}}
       />
       <DrawerItem
    icon={({color,size})=> (
        <Icon
        name="chart-timeline-variant"
        color={color}
        size={size}
        
        />

    )}
        label='Statistique'
        onPress={()=> {props.navigation.navigate('Statistique')}}
/>
<DrawerItem
    icon={({color,size})=> (
        <Icon
        name="map-marker-outline"
        color={color}
        size={size}
        
        />

    )}
        label='HeatMap'
        onPress={()=> {props.navigation.navigate('HeatMap')}}
/>
<DrawerItem
    icon={({color,size})=> (
        <Icon
        name="bell-outline"
        color={color}
        size={size}
        
        />

    )}
        label='Créer une alerte'
        onPress={()=> {props.navigation.navigate('Alerte')}}
/>
<DrawerItem
    icon={({color,size})=> (
        <Icon 
        name="lightbulb-on-outline"
        color={color}
        size={size}
        
        />

    )}
        label="Guide d'utilisation"
        onPress={()=> {props.navigation.navigate('Guide')}}
/>
<DrawerItem
    icon={({color,size})=> (
        <Icon 
        name="help-circle-outline"
        color={color}
        size={size}
        
        />

    )}
        label='A propos de nous'
        onPress={()=> {props.navigation.navigate('aPropos')}}
/>
        </Drawer.Section>
        <Drawer.Section title="Préférences">
            <TouchableRipple onPress={()=> {toggleTheme()}}>
                <View style={styles.preference}>
                    <Text>Dark Theme</Text>
                    <View pointerEvents="none">
                    <Switch value={isDarkTheme}/>
                    </View>
                </View>

            </TouchableRipple>
        </Drawer.Section>
    </View>
</DrawerContentScrollView>
<Drawer.Section style={StyleSheet.bottomDrawerSection}>
<DrawerItem
    icon={({color,size})=> (
        <Icon 
        name="exit-to-app"
        color={color}
        size={size}
        
        />

    )}
        label='Déconnexion'
        onPress={()=> {props.navigation.navigate('LoginScreen')}}
/>
    
</Drawer.Section>
        </View>
    );
}
const styles=StyleSheet.create({
    DrawerContent:{
        flex:1,
    },
    userInfoSection:{
        paddingLeft:20,
    },
    title:{
        fontSize:16,
        marginTop:3,
        fontWeight:'bold',
    },
    caption:{
        fontSize:14,
        lineHeight:14,
    },
    row:{
        marginTop:20,
        flexDirection:'row',
        alignItems:'center'
    },
    paragraph:{
        fontWeight:'bold',
        marginRight:3,
    },
    drawerSection:{
        marginTop:15,
    },
    bottomDrawerSection:{
        marginBottom:15,
        borderTopColor:'#f4f4f4',
        borderTopWidth:1
    },
    preference:{
        flexDirection:'row',
        justifyContent:'space-between',
        paddingVertical:12,
        paddingHorizontal:16,
    }
});
