import React from 'react';
import {
  StyleSheet,
  TextInput,
  Image,
  ActivityIndicator,
  Text,
  View,
  TouchableOpacity,
} from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { ScrollView } from 'react-native-gesture-handler';

const validationSchema = yup.object().shape({
  id: yup
  .string()
  .label('ID Administrateur')
  .required('ID Administrateur obligatoire.'),
  password: yup
    .string()
    .label('Mot de passe')
    .required('Mot de passe obligatoire.')
});

export  function AdminScreen({navigation}){
  return (
  <View style={styles.container}>
    <ScrollView>
    <Formik
      initialValues={{ id: '', password: '' }}
      onSubmit={(values, actions) => {
        alert(JSON.stringify(values));
        setTimeout(() => {
          actions.setSubmitting(false);
        }, 1000);
      }}
      validationSchema={validationSchema}
    >
      {(formikProps) => (
        <React.Fragment>
         <View>
         
          <Text style={styles.titre}>Administrateur</Text>
         
          <View style={styles.mini}>
                <Image
                    style={styles.imageAdmin}
                    source={require('../img/admin.png')}
                />
           
              <Text style={styles.textAdmin}>ID Administrateur</Text>
           
          </View>
          </View>
              <TextInput
                placeholder="Ecrivez votre ID"
                style={styles.textInput1}
                onChangeText={formikProps.handleChange('ID')}
                onBlur={formikProps.handleBlur('ID')}
                autoFocus
              />

            <Text style={{ color: 'red' }}>
              {formikProps.touched.id && formikProps.errors.id}
            </Text>
          

          <View style={styles.mini2}>
              <Image
                style={styles.imageAdmin}
                source={require('../img/cadenasn.png')}
               />
                <Text style={styles.textAdmin}>Mot de passe</Text>
          </View>
            
            <TextInput
              placeholder="Mot de passe"
              style={styles.textInput1}
              onChangeText={formikProps.handleChange('password')}
              onBlur={formikProps.handleBlur('password')}
              secureTextEntry
            />

            <Text style={{ color: 'red' }}>
              {formikProps.touched.password && formikProps.errors.password}
            </Text>
          
          

          <View style={styles.mini3}>
              
              {formikProps.isSubmitting ? (
                  <ActivityIndicator />
                  ) : (
                    <TouchableOpacity
                    onPress={formikProps.handleSubmit}>
                      <Text style={styles.textIdent}>S'identifier</Text>
                    </TouchableOpacity>
                   )}

          </View>
          <TouchableOpacity
             onPress={() => {navigation.navigate('MdpOubliéScreen')}}>
              <Text style={styles.guide}>Mot de passe oublié?</Text>
          </TouchableOpacity>

     <TouchableOpacity
          onPress={() => {navigation.navigate('SignUpScreen')}}>
          <Text style={styles.create}>Créer un compte.</Text>
      </TouchableOpacity>
      
        </React.Fragment>
      )}
      
    </Formik>
   </ScrollView>
  </View>
);}
const styles=StyleSheet.create({

  container:{ 
  flex:1,
  alignItems:'center',
  },
  text:{
      textAlign:'center', 
      marginTop:100,
      fontSize:17,
  },
mini:{
  flexDirection:'row',
  justifyContent:'center',
   marginTop:80,
   padding:5,
},
mini2:{
  flexDirection:'row',
  justifyContent:'center',
   marginTop:25,
   padding:3,
},
mini3:{
  flexDirection:'row',
  justifyContent:'center',
 borderRadius:15,
  backgroundColor:'#367ce5',
marginTop:25,
padding:5,
},
imageAdmin:{
  marginTop:2,
  height:24,
  width:20,
  borderRadius:15,
  marginLeft:8
},
textAdmin:{
  marginTop:2,
  marginLeft:10,
  fontSize:18,
  width:200,
  height:30,
  fontWeight:'bold',
  color:'black',
},
guide:{
  marginTop:30,
  fontSize:12,
  color:'#367ce5',
  borderBottomWidth:0.2,
  borderBottomColor:'#367ce5',
},
textInput1:{
  borderBottomWidth:1,
  height:35,
  width:200
},
titre:{
  fontSize:30,
  fontWeight:'bold',
  marginTop:150,
  color:'#367ce5',
},
textIdent:{
  marginTop:2,
  marginLeft:10,
  fontSize:18,
  color:'white',
  width:200,
  height:30,
  textAlign:'center',
},
create:{
  marginTop:80,
  fontSize:14,
  color:'#367ce5',
  
}
});
