import React from 'react';
import {
  SafeAreaView,
  StyleSheet,
  TextInput,
  Image,
  ActivityIndicator,
  Text,
  View,
  TouchableOpacity,
} from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { ScrollView } from 'react-native-gesture-handler';

const validationSchema = yup.object().shape({
  id: yup
  .string()
  .label('ID Administrateur')
  .required('ID Administrateur obligatoire.'),
  password: yup
    .string()
    .label('Mot de passe')
    .required('Mot de passe obligatoire.')
    .min(5,'Votre mot de passe est trop court') 
    .max(20,'Vous ne pouvez pas dépassser 20 lettres'),
  email:yup
  .string()
  .label('Email')
  .email('Format incorrecte.')
  .required('Email obligatoire.'),
  password_conf: yup
  .string()
  .label('Mot de passe')
  .oneOf([yup.ref('password'),null],'Mot de passe non identique')
  .required('Mot de passe obligatoire.')
  .min(5,'Votre mot de passe est trop court') 
  .max(20,'Vous ne pouvez pas dépassser 20 lettres')
});

export  function SignUpScreen({navigation}){
  return (

<View style={styles.container}>
<ScrollView>

<Text style={styles.Creer}>Créer Votre Compte</Text>
<Text style={styles.sec}>Cela ne prendra que quelques secondes.</Text>

<Formik
  initialValues={{ id: '', password: '',email: '' }}
  onSubmit={(values, actions) => {
    alert(JSON.stringify(values));
    setTimeout(() => {
      actions.setSubmitting(false);
    }, 1000);
  }}
  validationSchema={validationSchema}
  >
  {(formikProps) => (

    <React.Fragment>


  <View style={styles.champ}>
      <Image
        style={styles.imageAdmin}
        source={require('../img/admin.png')}
      />
      <Text style={styles.textAdmin}>ID Administrateur</Text>
  </View>
-    
    <TextInput 
        placeholder='Ecrivez votre ID' 
        placeholderTextColor='#808080'
        onChangeText={formikProps.handleChange('id')}
        onBlur={formikProps.handleBlur('id')}
        autoFocus
        style={styles.textInput1}
    />

        <Text style={{ color: 'red',marginLeft:10 }}>
          {formikProps.touched.id && formikProps.errors.id}
        </Text>

    <View style={styles.champ2}>
        <Image
          style={styles.imageAdmin}
          source={require('../img/cadenasn.png')}
        />
        <Text style={styles.textAdmin}>Mot de passe</Text>
    </View>

    <TextInput 
        placeholder='Ecrivez votre Mot de passe' 
        placeholderTextColor='#808080'
        secureTextEntry={true}
        style={styles.textInput1}
        onChangeText={formikProps.handleChange('password')}
        onBlur={formikProps.handleBlur('password')}
        autoFocus
    />
        <Text style={{ color: 'red',marginLeft:10 }}>
              {formikProps.touched.password && formikProps.errors.password}
         </Text>
   
    <View style={styles.champ2}>
          <Image
            style={styles.imageAdmin}
            source={require('../img/cadenasn.png')}
         />
          <Text style={styles.textAdmin}>Confirmation de Mot de passe</Text>
   </View>

   <TextInput 
        placeholder='Confirmer votre mot de passe' 
        placeholderTextColor='#808080'
        style={styles.textInput1}
        secureTextEntry={true}
        onChangeText={formikProps.handleChange('password_conf')}
        onBlur={formikProps.handleBlur('password_conf')}
        autoFocus

    />
            <Text style={{ color: 'red',marginLeft:10  }}>
              {formikProps.touched.password_conf && formikProps.errors.password_conf}
            </Text>


   <View style={styles.champ2}>
          <Image
            style={styles.imageAdmin}
            source={require('../img/email2.png')}
          />
           <Text style={styles.textAdmin}>Email</Text>
    </View>

    <TextInput 
        placeholder='Ecrivez votre Email' 
        placeholderTextColor='#808080'
        style={styles.textInput1}
        onChangeText={formikProps.handleChange('email')}
        onBlur={formikProps.handleBlur('email')}
        autoFocus
    />
        <Text style={{ color: 'red',marginLeft:10  }}>
              {formikProps.touched.email && formikProps.errors.email}
        </Text>

<View style={styles.mini3}>
              {formikProps.isSubmitting ? (
                  <ActivityIndicator />
                  ) : (
                  <TouchableOpacity
                      onPress={formikProps.handleSubmit}>
                    <Text style={styles.textIdent}>S'inscrire</Text>
                  </TouchableOpacity>
                   )}
</View>
     
     <Text style={styles.cgu}> En continuant, vous acceptez d'avoir lu et accepté nos CGU et notre politique de confidentialité.</Text>
    
    </React.Fragment>
)}
</Formik>
</ScrollView>
</View>

  );
  
}
  
  const styles=StyleSheet.create({
    
container:{
flex:1,
alignItems:'flex-start',
marginTop:100,
marginLeft:30,
},

Creer:{
  color:'#367ce5',
  fontSize:30,
  fontWeight:'bold'
},
sec:{
  color:'#367ce5',
},
imageAdmin:{
  height:26,
  width:20,
  borderRadius:15,
marginRight:7,
},
champ:{
  flexDirection:'row',
  justifyContent:'center',
   marginTop:40,
   padding:5,
},
champ2:{
  flexDirection:'row',
  justifyContent:'center',
   marginTop:10,
   padding:5,
},
textInput1:{
  borderBottomWidth:1,
  height:40,
  width:220,
  marginLeft:10,
},
textAdmin:{
  marginTop:0,
  fontWeight:'bold',
  fontSize:18
},
mini3:{
  marginLeft:90,
 borderRadius:15,
  backgroundColor:'#367ce5',
marginTop:60,
padding:4,
},

textIdent:{
  marginTop:2,
  
  fontSize:18,
  color:'white',
  width:150,
  height:30,
  textAlign:'center',
  
},
cgu:{
  textAlign:'center',
  fontSize:12,
  color:'#303030',
  marginTop:20,
  padding:10,
  marginRight:30,
  


}
  });
