import { StyleSheet, Text, View,Image,Button, TouchableOpacity} from 'react-native';


export  function LoginScreen({navigation}){
  return (
    <View style={styles.container}>
        <Image
        style={styles.image}
        source={require('../img/family.png')}
      
    />
      <Text style={styles.text}>SÃ©lectionnez Un Compte</Text>
  
 <TouchableOpacity
 onPress={() => {navigation.navigate('AdminScreen')}}>   
<View style={styles.mini}>
    <Image
    style={styles.imageAdmin}
    source={require('../img/cadenas.png')}
    />
    <Text style={styles.textAdmin}>Administrateur</Text>
    
    </View>
    </TouchableOpacity>

    <TouchableOpacity
 onPress={()=>{navigation.navigate('MagasinierScreen')}}>
<View style={styles.mini2} >
    <Image
    style={styles.imageAdmin}
    source={require('../img/admin.png')}
    />
    <Text style={styles.textMag}>Magasinier</Text>
  
</View>
</TouchableOpacity>

<TouchableOpacity
 onPress={() => {navigation.navigate('Guide')}}>
     
      <Text style={styles.guide}>Guide d'utilisation
      
      </Text>
    
    </TouchableOpacity>
</View>
  );}
  const styles=StyleSheet.create({

